import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  state = {
    discos_db: [],
    Disco: {
      Artista: 'Nine Inch Nails',
      Album: 'The Downward Spiral'
    }
  }

  componentDidMount(){
    this.getDiscos()
  }

  getDiscos = _ => {
    fetch('http://localhost:4000/Discos')
      .then(response => response.json())
      .then(response => this.setState({discos_db: response.data }))
      .catch(err => console.error(err))
  }

  addDiscos = _ => {
    const {Disco} = this.state
    fetch(`http://localhost:4000/discos_db/add?Artista=${Disco.Artista}&Album=${Disco.Album}`)
      .then(this.getDiscos)
      .catch(err => console.error(err))
  }

  renderDiscos = ({ id, Artista}) => <div key={id}>{Artista}</div>

  render(){
    const {discos_db} = this.state
    return (
      <div className="App">
        {discos_db.map(this.renderDiscos)}

      </div>
    )
  }
}


export default App;
